def Mission_1():
    # this is one of my boulder attractions
    drive_base.reset(0, 0)
    drive_base.straight(750)
    drive_base.turn(-35)
    drive_base.reset(0, 0)
    drive_base.straight(-25)
    drive_base.turn(-55)
    drive_base.reset(0, 0)
    drive_base.straight(-250)
    drive_base.straight(250)
    drive_base.arc(50, angle=90)
    drive_base.straight(35)
    drive_base.reset(0, 0)
    drive_base.turn(-55)
    drive_base.reset(0, 0)
    drive_base.straight(25)
